import React from 'react';
import { Route, Link } from 'react-router-dom';
import logo from './logo.svg';
import './App.css';
import Midi from './Midi';

/*
* Note: To begin application, navigate to midi-controller and type "npm start"
*
* Research: https://medium.com/swinginc/playing-with-midi-in-javascript-b6999f2913c3
https://www.smashingmagazine.com/2018/03/web-midi-api/
MidiWebAPI !!
*
* GitHub example: https://github.com/scott-ammon/web-midi-keyboard/blob/master/src/components/Keyboard.js
*/

function App() {
  return (
    <div className="App">
        <h1>Welcome to the MIDI App</h1>
        <p>MIDI stands for Musical Instrument Digital Interface. MIDI allows musical devices to be able to communicate with computers to pass information such as notes, velocity, and pitch.</p>
        <p>To get started, please connect a MIDI device by navigating to the MIDI page.</p>
        <Route exact path="/midi" component={Midi} />
        <Link to="/midi">Connect MIDI Device</Link>
    </div>
  );
}

export default App;
